import throughput from '../../images/throughput.svg'
import network from '../../images/netork.svg'
import cloud from '../../images/cloud-monitoring.svg'


const monitoringData = [
    {img: throughput, text: "Collect application metrics with APM monitoring. Understand how your application is performing and work to make a more efficient and stable application.", title:"APM"},

    {img: network, text: "Security is our top priority. Not only will your cloud setup be secure out of the gate, but we will also assist your development and operations teams in understanding best practice and maintaining a tight perimeter", title: "Network"},

    {img: cloud, text: "We take compliance and data governance very seriously. We will make sure that NDA's are signed, as well as ownership and viewing of data remains with your organization.", title: "Cloud"}

]

export default monitoringData;
