import React from "react";
import { Switch } from "react-router-dom";
import Home from '../Homepage/Home';
import Service from '../Services/Service';
import cloudbg from '../../images/cloud-detail.svg';
import devops from '../../images/devops.svg';
import { Route } from 'react-router-dom';
import iac from '../../images/iac.svg';
import people from '../../images/people.svg';
import security from '../../images/security.svg';
import data from '../../images/data-gov.svg';
import ScrollIntoView from '../ScrollIntoView'
import CloudData from '../json/cloudMigration.txt'

const Routes = () => {
    const migrateImg = [cloudData]

    return(
    <ScrollIntoView>    
        <Switch>
            <Route path="/" exact component={Home}/>
            <Route
            path='/cloud-migration'
            render={(props) => (
                <Service {...props} 
                heading = 'Migrate to the Cloud Hassle-free'
                description="ScaleHaus engineers will move your company to the cloud and hand over to you to manage. We offer long-term support so that your business thrives."
                bg={cloudbg} 
                img = {migrateImg}
                />
            )}
            />
            <Route
            path='/devops'
            render={(props) => (
                <Service {...props} 
                heading = 'DevOps'
                description='Devops Section of the site'
                bg={devops}
                img = {migrateImg}
                />
            )}
            />  
            <Route
            path='/iac'
            render={(props) => (
                <Service {...props} 
                heading = 'Infrastructure as Code'
                description='IaC segment'
                bg={iac}
                style ={{paddingBottom:0}}
                img = {migrateImg}
                />
            )}
            />  
        </Switch>
    </ScrollIntoView>
    )
}

export default Routes;
