import React from "react";
import { Switch } from "react-router-dom";
import Home from '../Homepage/Home';
import Service from '../Services/Service';
import cloudbg from '../../images/cloud-detail.svg';
import devops from '../../images/devops.svg';
import { Route } from 'react-router-dom';
import iac from '../../images/iac.svg';
import people from '../../images/people.svg';
import security from '../../images/security.svg';
import data from '../../images/data-gov.svg';

const Routes = () => {
    const migrateImg = [
        {img: people, text: "We will get you set up in the cloud. You keep the ownership and can lean on us for any troubleshooting"},
        {img: security, text: "Security is our top priority. Not only will your cloud setup be secure out of the gate, but we will also assist your development and operations teams in understanding best practice and maintaining a tight perimeter"},
        {img: data, text: "We take compliance and data governance very seriously. We will make sure that NDA's are signed, as well as ownership and viewing of data remains with your organization."},
    ]

    return(
    <Switch>
        <Route path="/" exact component={Home}/>
        <Route
        path='/cloud-migration'
        render={(props) => (
            <Service {...props} 
            heading = 'Migrate to the Cloud Hassle-free'
            description="ScaleHaus engineers will move your company to the cloud and hand over to you to manage. We offer long-term support so that your business thrives."
            bg={cloudbg} 
            img = {migrateImg}
            />
        )}
        />
        <Route
        path='/devops'
        render={(props) => (
            <Service {...props} 
            heading = 'DevOps'
            description='Devops Section of the site'
            bg={devops}
            img = {migrateImg}
            />
        )}
        />  
        <Route
        path='/iac'
        render={(props) => (
            <Service {...props} 
            heading = 'Infrastructure as Code'
            description='IaC segment'
            bg={iac}
            style ={{paddingBottom:0}}
            img = {migrateImg}
            />
        )}
        />  
    </Switch>
    )
}

export default Routes;
