import cicd from '../../images/devops_icon.svg'

const devopsData = [
    {img: cicd, text: "Partnering with ScaleHaus means you get our Continous Integration and Continuous Delivery. Never waste time on manual or faulty steps. Never lack confidence in code rolls.", title: "CI/CD"},

    {img: cicd, text: "Security is our top priority. Not only will your cloud setup be secure out of the gate, but we will also assist your development and operations teams in understanding best practice and maintaining a tight perimeter", title: "Lock and Key"},

    {img: cicd, text: "We take compliance and data governance very seriously. We will make sure that NDA's are signed, as well as ownership and viewing of data remains with your organization.", title: "Compliance"}
]

export default devopsData;
