import terraform from '../../images/terraform.svg'
import docker from '../../images/Moby-logo.svg'
import automate from '../../images/automate.svg'

const devopsData = [

    {img: terraform, text: "ScaleHaus engineers bring a wealth of terraform experience to you. We will get your environment infrastructure written, organized, and deployed.", title: "Hashicorp Terraform"},

    {img: docker, text: "If your organization has yet to join the container revolution, we are here to help.", title: "Docker"},

    {img: automate, text: "No more manual redundant tasks. All of us at ScaleHaus love to automate the mundane. We train and work through the day to day work load with your operations team to make sure no time is ever wasted on something that jenkins can handle.", title: "Automated Pipelines"}
]

export default devopsData;
